-- =====================================================
-- VERIFICACIÓN COMPLETA DE USUARIOS REGISTRADOS
-- Fecha: 2025-01-XX
-- Objetivo: Verificar que todos los usuarios tienen datos correctos en Supabase
-- =====================================================

-- =====================================================
-- 1. VERIFICAR USUARIOS EN auth.users
-- =====================================================

-- Contar usuarios totales en auth.users
SELECT 
  'Total usuarios en auth.users' as descripcion,
  COUNT(*) as cantidad
FROM auth.users;

-- Ver usuarios con sus datos básicos
SELECT 
  id,
  email,
  created_at,
  email_confirmed_at,
  last_sign_in_at,
  CASE 
    WHEN email_confirmed_at IS NULL THEN '❌ No confirmado'
    ELSE '✅ Confirmado'
  END as estado_email
FROM auth.users
ORDER BY created_at DESC
LIMIT 20;

-- =====================================================
-- 2. VERIFICAR TABLA users (public.users)
-- =====================================================

-- Contar usuarios en public.users
SELECT 
  'Total usuarios en public.users' as descripcion,
  COUNT(*) as cantidad
FROM public.users;

-- Ver usuarios con datos completos
SELECT 
  id,
  email,
  name,
  user_type,
  avatar,
  created_at,
  CASE 
    WHEN name IS NULL OR name = '' THEN '❌ Sin nombre'
    ELSE '✅ Con nombre'
  END as estado_nombre,
  CASE 
    WHEN user_type IS NULL THEN '❌ Sin tipo'
    ELSE '✅ Con tipo: ' || user_type
  END as estado_tipo
FROM public.users
ORDER BY created_at DESC
LIMIT 20;

-- =====================================================
-- 3. VERIFICAR TABLA user_profiles
-- =====================================================

-- Contar perfiles en user_profiles
SELECT 
  'Total perfiles en user_profiles' as descripcion,
  COUNT(*) as cantidad
FROM public.user_profiles;

-- Ver perfiles con datos completos
SELECT 
  user_id,
  display_name,
  avatar_url,
  bio,
  created_at,
  updated_at,
  CASE 
    WHEN display_name IS NULL OR display_name = '' THEN '❌ Sin display_name'
    ELSE '✅ Con display_name'
  END as estado_display_name,
  CASE 
    WHEN avatar_url IS NULL OR avatar_url = '' THEN '⚠️ Sin avatar'
    ELSE '✅ Con avatar'
  END as estado_avatar
FROM public.user_profiles
ORDER BY created_at DESC
LIMIT 20;

-- =====================================================
-- 4. VERIFICAR CONSISTENCIA ENTRE TABLAS
-- =====================================================

-- Usuarios en auth.users pero NO en public.users
SELECT 
  '❌ Usuarios en auth.users sin registro en public.users' as problema,
  COUNT(*) as cantidad
FROM auth.users au
LEFT JOIN public.users u ON au.id = u.id
WHERE u.id IS NULL;

-- Detalle de usuarios faltantes en public.users
SELECT 
  au.id,
  au.email,
  au.created_at,
  'Falta en public.users' as problema
FROM auth.users au
LEFT JOIN public.users u ON au.id = u.id
WHERE u.id IS NULL
ORDER BY au.created_at DESC;

-- Usuarios en public.users pero NO en auth.users (no debería pasar)
SELECT 
  '⚠️ Usuarios en public.users sin auth.users' as problema,
  COUNT(*) as cantidad
FROM public.users u
LEFT JOIN auth.users au ON u.id = au.id
WHERE au.id IS NULL;

-- Usuarios en public.users pero NO en user_profiles
SELECT 
  '⚠️ Usuarios en public.users sin user_profiles' as problema,
  COUNT(*) as cantidad
FROM public.users u
LEFT JOIN public.user_profiles up ON u.id = up.user_id
WHERE up.user_id IS NULL;

-- Detalle de usuarios sin perfil
SELECT 
  u.id,
  u.email,
  u.name,
  u.user_type,
  u.created_at,
  'Falta user_profile' as problema
FROM public.users u
LEFT JOIN public.user_profiles up ON u.id = up.user_id
WHERE up.user_id IS NULL
ORDER BY u.created_at DESC;

-- =====================================================
-- 5. VERIFICAR DATOS CRÍTICOS FALTANTES
-- =====================================================

-- Usuarios sin nombre en public.users
SELECT 
  '❌ Usuarios sin nombre en public.users' as problema,
  COUNT(*) as cantidad
FROM public.users
WHERE name IS NULL OR name = '';

-- Detalle de usuarios sin nombre
SELECT 
  id,
  email,
  user_type,
  created_at,
  'Sin nombre' as problema
FROM public.users
WHERE name IS NULL OR name = ''
ORDER BY created_at DESC;

-- Usuarios sin user_type
SELECT 
  '❌ Usuarios sin user_type' as problema,
  COUNT(*) as cantidad
FROM public.users
WHERE user_type IS NULL;

-- Detalle de usuarios sin user_type
SELECT 
  id,
  email,
  name,
  created_at,
  'Sin user_type' as problema
FROM public.users
WHERE user_type IS NULL
ORDER BY created_at DESC;

-- Usuarios sin display_name en user_profiles
SELECT 
  '⚠️ Usuarios sin display_name en user_profiles' as problema,
  COUNT(*) as cantidad
FROM public.user_profiles
WHERE display_name IS NULL OR display_name = '';

-- Detalle de usuarios sin display_name
SELECT 
  up.user_id,
  u.email,
  u.name,
  up.created_at,
  'Sin display_name' as problema
FROM public.user_profiles up
JOIN public.users u ON up.user_id = u.id
WHERE up.display_name IS NULL OR up.display_name = ''
ORDER BY up.created_at DESC;

-- =====================================================
-- 6. VERIFICAR USUARIOS INMOBILIARIAS
-- =====================================================

-- Contar inmobiliarias
SELECT 
  'Total inmobiliarias (user_type = agency)' as descripcion,
  COUNT(*) as cantidad
FROM public.users
WHERE user_type = 'agency';

-- Ver inmobiliarias con datos
SELECT 
  u.id,
  u.email,
  u.name,
  u.user_type,
  i.company_name,
  i.cuit,
  i.verified,
  CASE 
    WHEN i.id IS NULL THEN '❌ Sin datos en inmobiliarias'
    ELSE '✅ Con datos en inmobiliarias'
  END as estado_inmobiliaria
FROM public.users u
LEFT JOIN public.inmobiliarias i ON u.id = i.user_id
WHERE u.user_type = 'agency'
ORDER BY u.created_at DESC;

-- Inmobiliarias sin registro en tabla inmobiliarias
SELECT 
  '❌ Usuarios agency sin datos en tabla inmobiliarias' as problema,
  COUNT(*) as cantidad
FROM public.users u
LEFT JOIN public.inmobiliarias i ON u.id = i.user_id
WHERE u.user_type = 'agency' AND i.id IS NULL;

